import React, { Component } from 'react'
import Input from '../../components/Input/Input'
import RadioBtn from '../../components/RadioBtn/RadioBtn'
import Complement from '../../components/Complement/Complement'
import Comments from '../../components/Comments/Comments'
import ButtonNext from '../../components/ButtonNext/ButtonNext'
import './Origem.css'
import 'bootstrap/dist/css/bootstrap.css'

class Origem extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: '',
            showComplement: false,
            showComments: false
        }
    }

    selectedChangeOrigem = (event) => {
        const currentValue = event.target.value
        if (currentValue === 'apartamento') {
            this.setState({ showComplement: true })
            this.setState({ showComments: false })
        } else if (currentValue === 'casa') {
            this.setState({ showComments: true })
            this.setState({ showComplement: false })
        }
    }


    render() {
        return (
            <section className="origem">
                <h2>Origem</h2>
                <Input
                    htmlFor='adrStr'
                    id='adrStr'
                    type='text'
                    name='adrStr'>
                    Endere√ßo:
                </Input>
                <fieldset className="radio-btn radio-btn-origem" >
                    <input type="radio"
                        id="ap"
                        name="tipo-origem"
                        value="apartamento"
                        onChange={this.selectedChangeOrigem}
                    />
                    <label class="radio-btn" 
                    htmlFor="ap"
                    children="Apartamento"/>

                    <input type="radio"
                        id="casa"
                        name="tipo-origem"
                        value="casa"
                        onChange={this.selectedChangeOrigem}
                    />
                    <label class="radio-btn" htmlFor="casa"
                    children="Casa"/ >
                </fieldset>
                {
                    this.state.showComplement &&
                    <Complement />
                }
                {
                    this.state.showComments &&
                    <Comments />
                }
            </section>
        )
    }
}


export default Origem
