import React, { Component } from 'react'
import Input from '../../components/Input/Input'
import Dropdown from '../../components/Dropdown/Dropdown'
import Complement from '../../components/Complement/Complement'
import Comments from '../../components/Comments/Comments'
import ButtonNext from '../../components/ButtonNext/ButtonNext'

class PortageDate extends Component {
    constructor(props) {
        super(props)
        this.state = {
            value: 'default',
            periodSelection: false,
            dateSelection: false
        }
    }

    isFlexible = (event) => {
        if(event.target.value === 's') {
            this.setState({periodSelection: true})
            this.setState({dateSelection:false})
        }
        else if(event.target.value === 'n') {
            this.setState({dateSelection:true})
            this.setState({periodSelection: false})
        } else  if (event.target.value === 'default'){
            this.setState({periodSelection: false})
            this.setState({dateSelection: false})
        }
    }


    render() {
        return (
            <section>
                <Dropdown 
                htmlFor= 'date-flexible'
                id='date-flexible'
                name='date-flexible'
                value1='default'
                option1='Escolha uma opção'
                value2='s'
                option2='Sim'
                value3='n'
                option3='Não'
                onChange={this.isFlexible} >
                Sua data de mudança é flexível?
                </Dropdown>
            </section>
        )
    }
}

export default PortageDate
