import React, { Component } from 'react'
import './RadioBtn.css'

class RadioBtn extends Component {
    constructor(props) {
        super(props)
        this.state = ''
    }

    render() {
        return (
            <label className="radio-btn" htmlFor={props.htmlFor}><input type="radio" id={props.id} name={props.name} value={props.value} checked={this.state.selectedOption === 'option1'} onChange={this.handleOptionChange} />{props.children} </label>
        )
    }
}

export default RadioBtn
