import React from 'react'
import Input from '../../components/Input/Input'
import Complement from '../../components/Complement/Complement'
import Comments from '../../components/Comments/Comments'
import RadioBtn from '../../components/RadioBtn/RadioBtn'
import './Destino.css'

class Destino extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            value: '', 
            showComplementDestino: false,
            showCommentsDestino: false
        }
    }

    selectedChangeDestino = (event) => {
        console.log(event.target.value)
        const currentValue = event.target.value
        if (currentValue === 'apartamento') {
            this.setState({ showComplementDestino: true })
            this.setState({ showCommentsDestino: false })
        } else if (currentValue === 'casa') {
        this.setState({ showCommentsDestino: true })
        this.setState({ showComplementDestino: false })
    }
 }


    render() {
        return (
            <section className="origem">
                <h2>Destino</h2>
                <Input 
                htmlFor='adrStr'
                id='adrStr'
                type='text'
                name='adrStr'>
                Endere√ßo:
                </Input>
                <fieldset className="radio-btn radio-btn-destino" >
                    <input type="radio"
                        id="ap"
                        name="tipo-destino"
                        value="apartamento"
                        onChange={this.selectedChangeDestino}
                    />
                    <label class="radio-btn" 
                    htmlFor="ap"
                    children="Apartamento"/>

                    <input type="radio"
                        id="casa"
                        name="tipo-destino"
                        value="casa"
                        onChange={this.selectedChangeDestino}
                    />
                    <label class="radio-btn" htmlFor="casa"
                    children="Casa"/ >
                </fieldset>
                {
                    this.state.showComplementDestino &&
                    <Complement/>
                }
                {
                    this.state.showCommentsDestino &&
                    <Comments/>
                }
            </section>
        )
    }
}


export default Destino
