import React, { Component } from 'react'
import Origem from '../../components/Origem/Origem'
import Destino from '../../components/Destino/Destino'
import PortageDate from '../../components/PortageDate/PortageDate'
import ButtonNext from '../../components/ButtonNext/ButtonNext'
import './Pedido.css'



class Pedido extends Component {
    constructor(props) {
        super(props);
        this.state = {
            addressOrigin: undefined,
            addressDestiny: undefined,
            distance: undefined
        };
    };

    _getWeatherInfo = (city) => {
        const main = this;
        let query = null;
        main.setState({
            infoStatus: 'loading'
        });
        if (!city || city == '') {
          query = this.props.city;
        } else {
          query = city;
        }
        fetch(`http://api.openweathermap.org/data/2.5/weather?q=${query}&units=metric&appid=50a34e070dd5c09a99554b57ab7ea7e2`)
        .then( function(response) {
          return response;
        })
        .then( function(response) {
          setTimeout( function() {
            main.setState({
            infoStatus: 'loaded'
          });
          }, 300);
          return response.json();
        })
        .then( function(data) {
          main.setState({
            city: data.name,
            country: data.sys.country,
            temperature: data.main.temp,
            humidity: data.main.humidity,
            wind: data.wind.speed,
          });
        })
        .catch( function() {
          main.setState({
            infoStatus: 'error'
          });
        })
      };
      componentWillMount() {
        this._getWeatherInfo();
      };
      _handleSubmit = (event) => {
        event.preventDefault();
        this._getWeatherInfo(event.target.search.value);
      };

    handleChangeAddressOrigin = (event) => {
        this.setState({addressOrigin: event.target.value})
    }

    handleChangeAddressDestiny = (event) => {
        this.setState({addressDestiny: event.target.value})
    }

    getDistance() {
        const mapsApi = `https://maps.googleapis.com/maps/api/distancematrix/json?origins=${this.state.addressOrigin}&destinations=${this.state.addressDestiny}&departure_time=now&key=AIzaSyDdgczZxyGrNW9df2fl_D46_1naowL4OJE`
        fetch(mapsApi)
        .then(function(response) {
            return console.log(response);
        });
    }


    render() {
        return (
            <form className="pedido">
            <h1>Descubra o valor do seu frete</h1>
            <fieldset>
            <Origem
                addressOrigin={this.state.addressOrigin}
                onChange={this.handleChangeAddressOrigin}
                />
            </fieldset>
            <fieldset>
            <Destino
                addressOrigin={this.state.addressDestiny}
                onChange={this.handleChangeAddressDestiny}
            />
            </fieldset>
            <fieldset>
            <PortageDate/>
            </fieldset>
            <div className="btn">
            <ButtonNext url='/'>Pr√≥ximo</ButtonNext>
            </div>
            </form>
        )
    }
}

export default Pedido
