import React, { Component } from 'react'
import { items } from '../../data/items.js'
import './Checkbox.css'

class Checkbox extends Component {
    constructor(props){
        super(props)
    }

    insertInList(event) {
        let node = document.createElement("LI")
        let itemList = document.createTextNode(event.target.value)
        node.appendChild(itemList)
    }

    render() {
        return (
            <div className="checkbox-selectItens">
                {items.map((item) => {
                    return (  
                        <div>
                            <input
                                type="checkbox"
                                name={item.name}
                                id={item._id}
                                value={item.name}
                                />
                            <label 
                                htmlFor={item._id}>
                                    <img src={item.icon} alt={item.name}/>
                                    {item.name}
                            </label> 
                        </div>
                    )
                })}
            </div>
        )
    }
}

export default Checkbox
