import React from 'react'
import './RadioBtn.css'

function RadioBtn(props) {
    return (
        <label className="radio-btn" htmlFor={props.htmlFor}><input type="radio" id={props.id} name={props.name} value={props.value} checked={this.state.selectedOption === 'option1'} />{props.children} onChange={this.handleOptionChange}</label>
    )
}

export default RadioBtn
