            this.setState({periodSelection: false})
        } else  if (event.target.value === 'default'){
            this.setState({periodSelection: false})
            this.setState({dateSelection: false})
        }
    }*/


    render() {
        return (
            <section>
                <Input 
                htmlFor='date-selection'
                id='date-selection'
                type='date'
                name='date-selection'>
                Selecione as datas que você pode fazer sua mudança:
                </Input>
            </section>
        )
    }
}

export default PortageDate
