import React, { Component } from 'react'
import ButtonNext from '../../components/ButtonNext/ButtonNext';
import SampleBtn from '../../components/SampleBtn/SampleBtn';
import Input from '../../components/Input/Input'
import Modal from '../../components/Modal/Modal';
import './Home.css'

let campos = [
    {
    htmlFor:"nomeModal",
    id:"nomeModal",
    type:"text",
    name:"nomeModal",
    children: "Nome:"
},
    {
    htmlFor:"telModal",
    id:"telModal",
    type:"text",
    name:"telModal",
    children: "Telefone:"
}
]


class Home extends Component {
    constructor(props) {
        super(props)
        this.state = { open: false }
    }

    showModal = (event) => {
        event.preventDefault()
        this.setState({ open: true }) 
    }

    hideModal = (event) => {
        event.preventDefault()
        this.setState({ open: false })
    }

    render(){
        return (
            <main className="home">
                <section className="title-home">
                    <h1>Tão simples que nem parece mudança.</h1>
                </section>
                <section className="buttons-home">
                    <ButtonNext url="/pedido">Fazer um pedido</ButtonNext>
                    <SampleBtn onClick={this.showModal}>Falar com um consultor</SampleBtn>
                </section>
                    {this.state.open &&
                    <Modal 
                        h3="Precisa de uma ajudinha?"
                        h5="Deixe seu contato e em breve nossa equipe entrará em contato para cuidar da sua mudança." >
                        {campos.map((data) => {
                            return (
                                <form>
                                    <Input
                                        htmlFor={data.htmlFor}
                                        id={data.id}
                                        type={data.type}
                                        name={data.name} />
                                    <label 
                                        htmlFor={data.htmlFor} children={data.children} />
                                </form>
                            )
                        })}

                    </Modal>
                    }
            </main>
        )
    }
}

export default Home
